import{_ as e,c as d,o,ag as a}from"./chunks/framework.D7F4Kr4v.js";const h=JSON.parse('{"title":"Shortcodes","description":"naticore_related_posts, naticore_related_users, naticore_related_terms — attributes and examples.","frontmatter":{"title":"Shortcodes","description":"naticore_related_posts, naticore_related_users, naticore_related_terms — attributes and examples."},"headers":[],"relativePath":"api/shortcodes.md","filePath":"api/shortcodes.md","lastUpdated":1771061476000}'),s={name:"api/shortcodes.md"};function r(c,t,i,l,n,p){return o(),d("div",null,[...t[0]||(t[0]=[a(`<h1 id="shortcodes" tabindex="-1">Shortcodes <a class="header-anchor" href="#shortcodes" aria-label="Permalink to &quot;Shortcodes&quot;">​</a></h1><p>Use these shortcodes in posts, pages, or widgets. When <code>post_id</code> is omitted, the <strong>current post</strong> is used (in the loop).</p><hr><h2 id="naticore-related-posts" tabindex="-1">[naticore_related_posts] <a class="header-anchor" href="#naticore-related-posts" aria-label="Permalink to &quot;[naticore_related_posts]&quot;">​</a></h2><p>Shows related posts for the current (or given) post.</p><table tabindex="0"><thead><tr><th>Attribute</th><th>Values</th><th>Default</th><th>Description</th></tr></thead><tbody><tr><td><code>type</code></td><td>relation type slug</td><td><code>related_to</code></td><td>e.g. <code>related_to</code>, <code>parent_of</code>, <code>depends_on</code></td></tr><tr><td><code>limit</code></td><td>1–50</td><td><code>5</code></td><td>Max number of posts</td></tr><tr><td><code>order</code></td><td><code>date</code>, <code>title</code></td><td><code>date</code></td><td>Sort order</td></tr><tr><td><code>post_id</code></td><td>post ID or <code>0</code></td><td>current post</td><td>Source post; <code>0</code> = current</td></tr><tr><td><code>layout</code></td><td><code>list</code>, <code>grid</code></td><td><code>list</code></td><td>List or grid layout</td></tr><tr><td><code>title</code></td><td>text</td><td>“Related Content”</td><td>Heading above the list</td></tr><tr><td><code>class</code></td><td>CSS classes</td><td>—</td><td>Extra wrapper class(es)</td></tr><tr><td><code>show_thumbnail</code></td><td><code>0</code>, <code>1</code></td><td><code>0</code></td><td>Show post thumbnail</td></tr><tr><td><code>excerpt_length</code></td><td>number (words), <code>0</code> = none</td><td><code>0</code></td><td>Excerpt length</td></tr></tbody></table><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>[naticore_related_posts]</span></span>
<span class="line"><span>[naticore_related_posts type=&quot;related_to&quot; limit=&quot;6&quot; layout=&quot;grid&quot; title=&quot;You might also like&quot;]</span></span>
<span class="line"><span>[naticore_related_posts type=&quot;parent_of&quot; limit=&quot;10&quot; order=&quot;title&quot; show_thumbnail=&quot;1&quot; excerpt_length=&quot;15&quot;]</span></span>
<span class="line"><span>[naticore_related_posts post_id=&quot;123&quot; type=&quot;related_to&quot; limit=&quot;5&quot;]</span></span></code></pre></div><hr><h2 id="naticore-related-users" tabindex="-1">[naticore_related_users] <a class="header-anchor" href="#naticore-related-users" aria-label="Permalink to &quot;[naticore_related_users]&quot;">​</a></h2><p>Shows related users (e.g. authors, who bookmarked/favorited).</p><table tabindex="0"><thead><tr><th>Attribute</th><th>Values</th><th>Default</th><th>Description</th></tr></thead><tbody><tr><td><code>type</code></td><td>relation type slug</td><td><code>authored_by</code></td><td>e.g. <code>authored_by</code>, <code>favorite_posts</code>, <code>bookmarked_by</code></td></tr><tr><td><code>limit</code></td><td>1–50</td><td><code>5</code></td><td>Max number of users</td></tr><tr><td><code>order</code></td><td><code>date</code>, <code>name</code></td><td><code>date</code></td><td>Sort order</td></tr><tr><td><code>post_id</code></td><td>post ID or <code>0</code></td><td>current post</td><td>Source post; <code>0</code> = current</td></tr><tr><td><code>layout</code></td><td><code>list</code>, <code>grid</code></td><td><code>list</code></td><td>List or grid layout</td></tr><tr><td><code>title</code></td><td>text</td><td>“Related Users”</td><td>Heading above the list</td></tr><tr><td><code>class</code></td><td>CSS classes</td><td>—</td><td>Extra wrapper class(es)</td></tr></tbody></table><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>[naticore_related_users]</span></span>
<span class="line"><span>[naticore_related_users type=&quot;authored_by&quot; limit=&quot;5&quot; title=&quot;Contributors&quot;]</span></span>
<span class="line"><span>[naticore_related_users type=&quot;bookmarked_by&quot; limit=&quot;10&quot; layout=&quot;grid&quot; title=&quot;Saved by&quot;]</span></span></code></pre></div><hr><h2 id="naticore-related-terms" tabindex="-1">[naticore_related_terms] <a class="header-anchor" href="#naticore-related-terms" aria-label="Permalink to &quot;[naticore_related_terms]&quot;">​</a></h2><p>Shows related taxonomy terms for the current (or given) post.</p><table tabindex="0"><thead><tr><th>Attribute</th><th>Values</th><th>Default</th><th>Description</th></tr></thead><tbody><tr><td><code>type</code></td><td>relation type slug</td><td><code>categorized_as</code></td><td>e.g. <code>categorized_as</code></td></tr><tr><td><code>limit</code></td><td>1–50</td><td><code>5</code></td><td>Max number of terms</td></tr><tr><td><code>order</code></td><td><code>date</code>, <code>name</code></td><td><code>date</code></td><td>Sort order</td></tr><tr><td><code>post_id</code></td><td>post ID or <code>0</code></td><td>current post</td><td>Source post; <code>0</code> = current</td></tr><tr><td><code>layout</code></td><td><code>list</code>, <code>grid</code></td><td><code>list</code></td><td>List or grid layout</td></tr><tr><td><code>title</code></td><td>text</td><td>“Related Terms”</td><td>Heading above the list</td></tr><tr><td><code>class</code></td><td>CSS classes</td><td>—</td><td>Extra wrapper class(es)</td></tr></tbody></table><p><strong>Examples:</strong></p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span>[naticore_related_terms]</span></span>
<span class="line"><span>[naticore_related_terms type=&quot;categorized_as&quot; limit=&quot;5&quot; layout=&quot;grid&quot; title=&quot;In these collections&quot;]</span></span>
<span class="line"><span>[naticore_related_terms post_id=&quot;456&quot; type=&quot;categorized_as&quot; limit=&quot;10&quot;]</span></span></code></pre></div><hr><h2 id="styling" tabindex="-1">Styling <a class="header-anchor" href="#styling" aria-label="Permalink to &quot;Styling&quot;">​</a></h2><p>When any of these shortcodes is used, the plugin enqueues <code>shortcodes.css</code>. You can override with your own CSS by targeting:</p><ul><li><code>.naticore-related-posts</code>, <code>.naticore-related-users</code>, <code>.naticore-related-terms</code></li><li><code>.naticore-related-list</code>, <code>.naticore-related-grid</code></li><li>Optional <code>class</code> attribute for your own wrapper class</li></ul><hr><h2 id="widget" tabindex="-1">Widget <a class="header-anchor" href="#widget" aria-label="Permalink to &quot;Widget&quot;">​</a></h2><p>The <strong>Related Content (NCR)</strong> widget uses the same output as the posts shortcode. Add it under <strong>Appearance → Widgets</strong>; options include title, relationship type, number of items, order, and optional post ID. See <a href="/WP-Native-Content-Relationships/guide/widget.html">Widget</a>.</p><hr><h2 id="see-also" tabindex="-1">See also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;See also&quot;">​</a></h2><ul><li><a href="/WP-Native-Content-Relationships/guide/use-cases.html">Use cases</a> — Examples by scenario</li><li><a href="/WP-Native-Content-Relationships/guide/widget.html">Widget</a> — Sidebar widget</li><li><a href="/WP-Native-Content-Relationships/guide/quick-start.html">Quick Start</a> — First shortcode in the editor</li><li><a href="/WP-Native-Content-Relationships/api/php-api.html">PHP API</a> — Same data via <code>ncr_get_related()</code></li></ul>`,30)])])}const _=e(s,[["render",r]]);export{h as __pageData,_ as default};
