import{_ as e,c as o,o as a,ag as r}from"./chunks/framework.D7F4Kr4v.js";const c=JSON.parse('{"title":"Related Content Widget","description":"Sidebar widget to display related posts — Appearance → Widgets, options and output.","frontmatter":{"title":"Related Content Widget","description":"Sidebar widget to display related posts — Appearance → Widgets, options and output."},"headers":[],"relativePath":"guide/widget.md","filePath":"guide/widget.md","lastUpdated":1771061476000}'),i={name:"guide/widget.md"};function s(d,t,n,l,h,p){return a(),o("div",null,[...t[0]||(t[0]=[r('<h1 id="related-content-widget" tabindex="-1">Related Content Widget <a class="header-anchor" href="#related-content-widget" aria-label="Permalink to &quot;Related Content Widget&quot;">​</a></h1><p>The <strong>Related Content (NCR)</strong> widget displays related posts in sidebars or any widget area. It uses the same data and markup as the <a href="/WP-Native-Content-Relationships/api/shortcodes.html#naticore_related_posts">shortcode</a>; only the configuration UI is different.</p><hr><h2 id="where-to-add-it" tabindex="-1">Where to add it <a class="header-anchor" href="#where-to-add-it" aria-label="Permalink to &quot;Where to add it&quot;">​</a></h2><p><strong>Appearance → Widgets</strong> (or <strong>Appearance → Customize → Widgets</strong> in block themes). Add the widget <strong>Related Content (NCR)</strong> to a sidebar or widget block.</p><hr><h2 id="widget-options" tabindex="-1">Widget options <a class="header-anchor" href="#widget-options" aria-label="Permalink to &quot;Widget options&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Option</th><th>Description</th></tr></thead><tbody><tr><td><strong>Title</strong></td><td>Optional widget title. Leave blank to use the shortcode default heading (“Related Content”).</td></tr><tr><td><strong>Relationship type</strong></td><td>e.g. Related To, Parent Of, Depends On. Same as shortcode <code>type</code>.</td></tr><tr><td><strong>Number of items</strong></td><td>1–50. Same as shortcode <code>limit</code>.</td></tr><tr><td><strong>Order by</strong></td><td>Date or Title. Same as shortcode <code>order</code>.</td></tr><tr><td><strong>Post ID (optional)</strong></td><td>Leave <strong>0</strong> to use the current post (in the loop). Set a post ID to show relations for a specific post.</td></tr></tbody></table><hr><h2 id="behavior" tabindex="-1">Behavior <a class="header-anchor" href="#behavior" aria-label="Permalink to &quot;Behavior&quot;">​</a></h2><ul><li><strong>Context:</strong> On single post/page, “current post” is the one being viewed. On archives or when no post is in context, set <strong>Post ID</strong> explicitly or the widget may output nothing.</li><li><strong>Output:</strong> Same HTML and CSS as <code>[naticore_related_posts]</code> (list layout, same wrapper classes). Shortcode CSS is enqueued when the widget renders.</li><li><strong>Empty:</strong> If there are no related posts, the widget outputs nothing (no empty box).</li></ul><hr><h2 id="example" tabindex="-1">Example <a class="header-anchor" href="#example" aria-label="Permalink to &quot;Example&quot;">​</a></h2><p>Add “Related Content (NCR)” to the main sidebar with:</p><ul><li>Title: <strong>You might also like</strong></li><li>Relationship type: <strong>Related To</strong></li><li>Number of items: <strong>5</strong></li><li>Order by: <strong>Date</strong></li><li>Post ID: <strong>0</strong></li></ul><p>Every single post will show up to 5 “related to” posts in the sidebar.</p><hr><h2 id="see-also" tabindex="-1">See also <a class="header-anchor" href="#see-also" aria-label="Permalink to &quot;See also&quot;">​</a></h2><ul><li><a href="/WP-Native-Content-Relationships/api/shortcodes.html">Shortcodes</a> — Same data via <code>[naticore_related_posts]</code></li><li><a href="/WP-Native-Content-Relationships/guide/quick-start.html">Quick Start</a> — First relationship and display</li></ul>',19)])])}const u=e(i,[["render",s]]);export{c as __pageData,u as default};
